import { useState, useEffect, useCallback } from 'react';
import {
  Container,
  Grid,
  Box,
  Button,
  Alert,
  Typography,
  CircularProgress
} from '@mui/material';
import {
  MonetizationOn,
  People,
  PlayCircle,
  ArrowForward
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import api from '../../utils/api';
import PageHeader from '../../components/common/PageHeader';
import StatCard from '../../components/common/StatCard';
import { useAuth } from '../../context/AuthContext';
import { useNotification } from '../../context/NotificationContext';

export default function Dashboard() {
  const [stats, setStats] = useState({
    points: 0,
    videoPoints: 0,      // Add videoPoints
    welcomeBonus: 3000,  // Add welcomeBonus
    earnings: 0,
    referralEarnings: 0,
    videosWatched: 0,
    referrals: 0,
    activeReferrals: 0
  });
  
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useAuth();
  const navigate = useNavigate();
  const { showNotification } = useNotification();
  const fetchDashboardData = useCallback(async () => {
    try {
      const [statsRes, referralRes, videosRes] = await Promise.all([
        api.get('/users/stats'),
        api.get('/users/referral-info'),
        api.get('/users/watched-videos')
      ]);
      
      if (statsRes.data && referralRes.data) {
        const referralEarnings = referralRes.data.activeReferrals * 2800;
        const videoPoints = videosRes.data.reduce((total, video) => total + (video.pointsEarned || 0), 0);
        const totalPoints = videoPoints + stats.welcomeBonus;
        const watchedVideosCount = videosRes.data.length || 0;
        
        setStats({
          ...statsRes.data,
          videoPoints,
          points: totalPoints,
          earnings: referralEarnings,
          referralEarnings,
          activeReferrals: referralRes.data.activeReferrals,
          referrals: referralRes.data.totalReferrals,
          videosWatched: watchedVideosCount
        });
      }
    } catch (err) {
      console.error('Dashboard error:', err);
      const errorMessage = err.response?.data?.error || 'Failed to load dashboard data';
      setError(errorMessage);
      showNotification(errorMessage, 'error');
    } finally {
      setLoading(false);
    }
  }, [showNotification, stats.welcomeBonus]);
  
  useEffect(() => {
    fetchDashboardData();
  }, [fetchDashboardData]);
  if (loading) {
    return (
      <Box 
        display="flex" 
        justifyContent="center" 
        alignItems="center" 
        minHeight="400px"
      >
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Alert severity="error" sx={{ mt: 3 }}>
          {error}
        </Alert>
      </Container>
    );
  }

  return (
    <Container>
      <PageHeader 
        title={`Welcome back, ${user.fullName}!`}
        action={
          !user.isActive && (
            <Button
              variant="contained"
              color="primary"
              onClick={() => navigate('/app/activate')}
            >
              Activate Account
            </Button>
          )
        }
      />

      {!user.isActive && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          Your account is not activated. Activate your account to start earning points and rewards.
        </Alert>
      )}

      <Grid container spacing={3} mb={4}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Points"
            value={`${stats.points.toLocaleString()} pts`}
            subtitle={`Including ${stats.welcomeBonus.toLocaleString()} welcome bonus`}
            icon={MonetizationOn}
            color="primary"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Video Points Earned"
            value={`${stats.videoPoints.toLocaleString()} pts`}
            subtitle={`From ${stats.videosWatched} videos watched`}
            icon={PlayCircle}
            color="info"
            action={
              user.isActive && {
                label: 'Watch Videos',
                onClick: () => navigate('/app/videos')
              }
            }
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Earnings"
            value={`RWF ${stats.earnings.toLocaleString()}`}
            subtitle={`From ${stats.activeReferrals} active referrals`}
            icon={MonetizationOn}
            color="success"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Team Members"
            value={stats.referrals}
            subtitle={`${stats.activeReferrals} active members`}
            icon={People}
            color="warning"
            action={{
              label: 'View Team',
              onClick: () => navigate('/app/referrals')
            }}
          />
        </Grid>
      </Grid>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Box
            sx={{
              p: 3,
              bgcolor: 'background.paper',
              borderRadius: 1,
              boxShadow: 1
            }}
          >
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
              <Box>
                <Typography variant="h6" gutterBottom>
                  Your Referral Link
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Share this link to earn RWF 2,800 per active member
                </Typography>
              </Box>
              <Button
                variant="outlined"
                endIcon={<ArrowForward />}
                onClick={() => navigate('/app/referrals')}
              >
                View Details
              </Button>
            </Box>
            <Box
              sx={{
                p: 2,
                bgcolor: 'grey.50',
                borderRadius: 1,
                wordBreak: 'break-all'
              }}
            >
              <Typography variant="body2">
                {`${window.location.origin}/register?ref=${user?.referralCode || ''}`}
              </Typography>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
}
