import { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Box,
  Button,
  Alert,
  Typography,
  CircularProgress
} from '@mui/material';
import {
  MonetizationOn,
  People,
  PlayCircle,
  ArrowForward
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import api from '../../utils/api';
import PageHeader from '../../components/common/PageHeader';
import StatCard from '../../components/common/StatCard';
import { useAuth } from '../../context/AuthContext';
import { useNotification } from '../../context/NotificationContext';

export default function Dashboard() {
  const [stats, setStats] = useState({
    points: 0,          // Set default value to 0
    earnings: 0,        // Set default value to 0
    referralEarnings: 0, // Set default value to 0
    videosWatched: 0,   // Set default value to 0
    referrals: 0,       // Set default value to 0
    activeReferrals: 0  // Set default value to 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useAuth();
  const navigate = useNavigate();
  const { showNotification } = useNotification();

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // Fetch all required data in parallel
        const [statsRes, referralRes, videosRes] = await Promise.all([
          api.get('/users/stats'),
          api.get('/users/referral-info'),
          api.get('/users/watched-videos')
        ]);
        
        if (statsRes.data && referralRes.data) {
          const referralEarnings = referralRes.data.activeReferrals * 2800;
          const totalEarnings = referralEarnings;
          const watchedVideosCount = videosRes.data.length || 0;
          
          setStats({
            ...statsRes.data,
            earnings: totalEarnings,
            referralEarnings: referralEarnings,
            activeReferrals: referralRes.data.activeReferrals,
            referrals: referralRes.data.totalReferrals,
            videosWatched: watchedVideosCount // Real count of watched videos
          });
        }
      } catch (err) {
        console.error('Dashboard error:', err);
        const errorMessage = err.response?.data?.error || 'Failed to load dashboard data';
        setError(errorMessage);
        showNotification(errorMessage, 'error');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [showNotification]);

  if (loading) {
    return (
      <Box 
        display="flex" 
        justifyContent="center" 
        alignItems="center" 
        minHeight="400px"
      >
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Alert severity="error" sx={{ mt: 3 }}>
          {error}
        </Alert>
      </Container>
    );
  }

  return (
    <Container>
      <PageHeader 
        title={`Welcome back, ${user.fullName}!`}
        action={
          !user.isActive && (
            <Button
              variant="contained"
              color="primary"
              onClick={() => navigate('/app/activate')}
            >
              Activate Account
            </Button>
          )
        }
      />

      {!user.isActive && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          Your account is not activated. Activate your account to start earning points and rewards.
        </Alert>
      )}

      <Grid container spacing={3} mb={4}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Available Points"
            value={`RWF ${(stats?.points || 0).toLocaleString()}`}
            icon={MonetizationOn}
            color="primary"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Earnings"
            value={`RWF ${(stats?.earnings || 0).toLocaleString()}`}
            subtitle={stats?.referralEarnings > 0 ? 
              `Including RWF ${(stats?.referralEarnings || 0).toLocaleString()} from referrals` : 
              'Start referring to earn more!'
            }
            icon={MonetizationOn}
            color="success"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Videos Watched"
            value={stats?.videosWatched || 0}
            icon={PlayCircle}
            color="info"
            action={stats?.videosWatched > 0 && {
              label: 'Watch More',
              onClick: () => navigate('/app/videos')
            }}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Team Members"
            value={stats?.referrals || 0}
            icon={People}
            color="warning"
            subtitle={`${stats?.activeReferrals || 0} active members`}
            action={{
              label: 'View Team',
              onClick: () => navigate('/app/referrals')
            }}
          />
        </Grid>
      </Grid>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Box
            sx={{
              p: 3,
              bgcolor: 'background.paper',
              borderRadius: 1,
              boxShadow: 1
            }}
          >
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
              <Box>
                <Typography variant="h6" gutterBottom>
                  Your Referral Link
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Share this link to earn RWF 2,800 per active member
                </Typography>
              </Box>
              <Button
                variant="outlined"
                endIcon={<ArrowForward />}
                onClick={() => navigate('/app/referrals')}
              >
                View Details
              </Button>
            </Box>
            <Box
              sx={{
                p: 2,
                bgcolor: 'grey.50',
                borderRadius: 1,
                wordBreak: 'break-all'
              }}
            >
              <Typography variant="body2">
                {`${window.location.origin}/register?ref=${user?.referralCode || ''}`}
              </Typography>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
}
