import api from './api';

export const createWithdrawal = async (withdrawalData) => {
  const response = await api.post('/withdrawals', withdrawalData);
  return response.data;
};

export const getUserWithdrawals = async () => {
  const response = await api.get('/withdrawals/my-withdrawals');
  return response.data;
};

export const getAllWithdrawals = async () => {
  const response = await api.get('/withdrawals/admin/all');
  return response.data;
};

export const getPendingWithdrawals = async () => {
  const response = await api.get('/withdrawals/admin/pending');
  return response.data;
};

export const processWithdrawal = async (id, data) => {
  const response = await api.put(`/withdrawals/admin/${id}`, data);
  return response.data;
};
