import { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Box,
  MenuItem,
  CircularProgress,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Chip,
  Divider
} from '@mui/material';
import { MonetizationOn } from '@mui/icons-material';
import { useAuth } from '../../context/AuthContext';
import { useNotification } from '../../context/NotificationContext';
import api from '../../utils/api';
import PageHeader from '../../components/common/PageHeader';

const PAYMENT_METHODS = [
  { value: 'momo', label: 'Mobile Money' },
  { value: 'bank', label: 'Bank Transfer' }
];

export default function Withdraw() {
  const [withdrawals, setWithdrawals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [earnings, setEarnings] = useState({
    referralEarnings: 0,
    totalEarnings: 0,
    activeReferrals: 0
  });
  const [formData, setFormData] = useState({
    amount: '',
    paymentMethod: 'momo',
    accountNumber: '',
    accountName: ''
  });

  const { user } = useAuth();
  const { showNotification } = useNotification();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [withdrawalsRes, referralRes] = await Promise.all([
        api.get('/withdrawals'),
        api.get('/users/referral-info')
      ]);

      // Ensure we have an array of withdrawals
      const withdrawalsData = withdrawalsRes.data.data || [];
      setWithdrawals(Array.isArray(withdrawalsData) ? withdrawalsData : []);

      // Calculate earnings from referrals
      const referralEarnings = referralRes.data.activeReferrals * 2800;
      setEarnings({
        referralEarnings,
        totalEarnings: referralEarnings, // Total earnings is referral earnings
        activeReferrals: referralRes.data.activeReferrals
      });
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to fetch data');
      showNotification('Failed to fetch withdrawal data', 'error');
      setWithdrawals([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate withdrawal amount
    if (Number(formData.amount) > earnings.totalEarnings) {
      setError('Withdrawal amount cannot exceed your total earnings');
      return;
    }

    if (Number(formData.amount) < 5000) {
      setError('Minimum withdrawal amount is RWF 5,000');
      return;
    }

    setSubmitting(true);
    setError(null);

    try {
      await api.post('/withdrawals', formData);
      showNotification('Withdrawal request submitted successfully', 'success');
      fetchData(); // Refresh data
      setFormData({
        amount: '',
        paymentMethod: 'momo',
        accountNumber: '',
        accountName: ''
      });
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to submit withdrawal request');
      showNotification(err.response?.data?.error || 'Failed to submit withdrawal request', 'error');
    } finally {
      setSubmitting(false);
    }
  };

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'success';
      case 'rejected': return 'error';
      default: return 'warning';
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container>
      <PageHeader 
        title="Withdraw Earnings" 
        subtitle="Request withdrawal of your earnings from referrals"
      />

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Available Balance
              </Typography>
              <Typography variant="h4" color="primary" gutterBottom>
                RWF {earnings.totalEarnings.toLocaleString()}
              </Typography>

              <Box sx={{ my: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Earnings Breakdown:
                </Typography>
                <Typography variant="body1">
                  • Team Earnings: RWF {earnings.referralEarnings.toLocaleString()} 
                  ({earnings.activeReferrals} active × 2,800 RWF)
                </Typography>
              </Box>

              <Divider sx={{ my: 2 }} />

              <Typography variant="body2" color="text.secondary">
                Minimum withdrawal: RWF 5,000
              </Typography>

              {error && (
                <Alert severity="error" sx={{ mt: 2 }}>
                  {error}
                </Alert>
              )}

              <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
                <TextField
                  fullWidth
                  label="Amount (RWF)"
                  name="amount"
                  type="number"
                  value={formData.amount}
                  onChange={handleChange}
                  required
                  sx={{ mb: 2 }}
                  inputProps={{
                    min: 5000,
                    max: earnings.totalEarnings
                  }}
                />
                <TextField
                  fullWidth
                  select
                  label="Payment Method"
                  name="paymentMethod"
                  value={formData.paymentMethod}
                  onChange={handleChange}
                  required
                  sx={{ mb: 2 }}
                >
                  {PAYMENT_METHODS.map(option => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
                <TextField
                  fullWidth
                  label="Account Number"
                  name="accountNumber"
                  value={formData.accountNumber}
                  onChange={handleChange}
                  required
                  sx={{ mb: 2 }}
                />
                <TextField
                  fullWidth
                  label="Account Name"
                  name="accountName"
                  value={formData.accountName}
                  onChange={handleChange}
                  required
                  sx={{ mb: 2 }}
                />
                <Button
                  type="submit"
                  variant="contained"
                  fullWidth
                  disabled={submitting || earnings.totalEarnings < 5000}
                  startIcon={<MonetizationOn />}
                >
                  {submitting ? 'Submitting...' : 'Request Withdrawal'}
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom>
            Withdrawal History
          </Typography>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Date</TableCell>
                  <TableCell align="right">Amount</TableCell>
                  <TableCell align="center">Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {Array.isArray(withdrawals) && withdrawals.length > 0 ? (
                  withdrawals.map((withdrawal) => (
                    <TableRow key={withdrawal._id}>
                      <TableCell>
                        {new Date(withdrawal.createdAt).toLocaleDateString()}
                      </TableCell>
                      <TableCell align="right">
                        RWF {withdrawal.amount.toLocaleString()}
                      </TableCell>
                      <TableCell align="center">
                        <Chip
                          label={withdrawal.status}
                          color={getStatusColor(withdrawal.status)}
                          size="small"
                        />
                      </TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={3} align="center">
                      <Typography variant="body2" color="text.secondary" py={3}>
                        No withdrawal history
                      </Typography>
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </Grid>
      </Grid>
    </Container>
  );
}
