import { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  Button,
  Box,
  Chip,
  CircularProgress,
  Alert,
  LinearProgress
} from '@mui/material';
import {
  PlayCircle,
  MonetizationOn,
  Person,
  Timer,
  CheckCircle
} from '@mui/icons-material';
import api from '../../utils/api';
import { useAuth } from '../../context/AuthContext';
import { useNotification } from '../../context/NotificationContext';
import PageHeader from '../../components/common/PageHeader';
import VideoPlayer from '../../components/VideoPlayer';

export default function Videos() {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const { user } = useAuth();
  const { showNotification } = useNotification();

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await api.get('/videos/available');
      if (response.data && Array.isArray(response.data)) {
        setVideos(response.data);
      } else {
        throw new Error('Invalid response format');
      }
    } catch (err) {
      setError('Failed to load videos');
      showNotification('Failed to load videos', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleWatchVideo = (video) => {
    if (!user.isActive) {
      showNotification('Please activate your account to watch videos', 'warning');
      return;
    }
    setSelectedVideo(video);
  };

  const handleVideoComplete = async () => {
    try {
      const response = await api.post(`/videos/${selectedVideo._id}/complete`, {
        watchTime: selectedVideo.minimumWatchTime
      });
      
      showNotification(`Congratulations! You earned ${selectedVideo.pointsReward} points!`, 'success');
      setSelectedVideo(null);
      fetchVideos(); // Refresh the video list
    } catch (err) {
      showNotification(
        err.response?.data?.message || 'Failed to record video completion', 
        'error'
      );
    }
  };

  const extractVideoId = (url) => {
    try {
      const urlObj = new URL(url);
      if (urlObj.hostname.includes('youtube.com')) {
        return urlObj.searchParams.get('v');
      } else if (urlObj.hostname.includes('youtu.be')) {
        return urlObj.pathname.slice(1);
      }
    } catch (error) {
      console.error('Error extracting video ID:', error);
    }
    return null;
  };

  const isVideoCompleted = (video) => {
    return video.watchedBy?.some(watch => 
      watch.user === user._id && watch.completedAt
    );
  };

  const renderVideoStatus = (video) => {
    if (isVideoCompleted(video)) {
      return (
        <Chip
          icon={<CheckCircle />}
          label="Completed"
          color="success"
          size="small"
          sx={{ mb: 1 }}
        />
      );
    }
    return null;
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Alert severity="error" sx={{ mt: 3 }}>
          {error}
        </Alert>
      </Container>
    );
  }

  return (
    <Container>
      <PageHeader 
        title="Available Videos" 
        subtitle="Watch videos to earn points"
      />

      {!user.isActive && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          Activate your account to start earning points from watching videos.
        </Alert>
      )}

      <Grid container spacing={3}>
        {videos.map((video) => (
          <Grid item xs={12} sm={6} md={4} key={video._id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <Box sx={{ position: 'relative' }}>
                <CardMedia
                  component="img"
                  height="180"
                  image={`https://img.youtube.com/vi/${extractVideoId(video.youtubeUrl)}/hqdefault.jpg`}
                  alt={video.title}
                  sx={{ cursor: 'pointer' }}
                  onClick={() => handleWatchVideo(video)}
                />
                {isVideoCompleted(video) && (
                  <Box
                    sx={{
                      position: 'absolute',
                      top: 10,
                      right: 10,
                      bgcolor: 'success.main',
                      color: 'white',
                      borderRadius: '50%',
                      p: 0.5
                    }}
                  >
                    <CheckCircle />
                  </Box>
                )}
              </Box>
              <CardContent sx={{ flexGrow: 1 }}>
                {renderVideoStatus(video)}
                <Typography gutterBottom variant="h6" component="div" noWrap>
                  {video.title}
                </Typography>
                <Box display="flex" alignItems="center" mb={1}>
                  <Timer sx={{ mr: 1, fontSize: 20 }} />
                  <Typography variant="body2" color="text.secondary">
                    Min. watch time: {video.minimumWatchTime}s
                  </Typography>
                </Box>
                <Box display="flex" alignItems="center">
                  <MonetizationOn sx={{ mr: 1, fontSize: 20 }} />
                  <Typography variant="body2" color="text.secondary">
                    {video.pointsReward} points
                  </Typography>
                </Box>
              </CardContent>
              <CardActions>
                <Button
                  fullWidth
                  variant={isVideoCompleted(video) ? "outlined" : "contained"}
                  startIcon={isVideoCompleted(video) ? <CheckCircle /> : <PlayCircle />}
                  onClick={() => handleWatchVideo(video)}
                  disabled={!user.isActive}
                  color={isVideoCompleted(video) ? "success" : "primary"}
                >
                  {isVideoCompleted(video) ? 'Watch Again' : 'Watch & Earn'}
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}

        {videos.length === 0 && (
          <Grid item xs={12}>
            <Box textAlign="center" py={4}>
              <Typography variant="h6" color="text.secondary">
                No videos available at the moment
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Please check back later for new videos
              </Typography>
            </Box>
          </Grid>
        )}
      </Grid>

      {selectedVideo && (
        <VideoPlayer
          videoId={extractVideoId(selectedVideo.youtubeUrl)}
          minimumWatchTime={selectedVideo.minimumWatchTime}
          onComplete={handleVideoComplete}
          onClose={() => setSelectedVideo(null)}
          title={selectedVideo.title}
          isCompleted={isVideoCompleted(selectedVideo)}
        />
      )}
    </Container>
  );
}
