import { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  IconButton,
  Chip,
  CircularProgress,
  TextField,
  InputAdornment,
  Button,
  Tooltip,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Grid,
  Card,
  CardContent,
  Tabs,
  Tab,
  Alert,
  Avatar,
  Badge,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Divider
} from '@mui/material';
import {
  CheckCircle,
  Cancel,
  Warning,
  Search,
  Visibility,
  ContentCopy,
  Schedule,
  Person,
  Receipt,
  ExpandMore,
  Group,
  AssignmentTurnedIn,
  Security
} from '@mui/icons-material';
import { format, formatDistanceToNow } from 'date-fns';
import api from '../../utils/api';
import { useNotification } from '../../context/NotificationContext';
import { useDebounce } from '../../hooks/useDebounce';

export default function TransactionApprovals() {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [search, setSearch] = useState('');
  const [pagination, setPagination] = useState({ page: 0, limit: 10, total: 0 });
  const [activeTab, setActiveTab] = useState(0);
  const [stats, setStats] = useState({
    pending: 0,
    approved: 0,
    rejected: 0,
    duplicates: 0
  });
  
  // Transaction detail dialog
  const [detailDialogOpen, setDetailDialogOpen] = useState(false);
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [transactionDetails, setTransactionDetails] = useState(null);
  
  // Process dialog
  const [processDialogOpen, setProcessDialogOpen] = useState(false);
  const [processingTransaction, setProcessingTransaction] = useState(null);
  const [processNotes, setProcessNotes] = useState('');
  const [processLoading, setProcessLoading] = useState(false);

  const { showNotification } = useNotification();
  const debouncedSearch = useDebounce(search, 500);

  const tabFilters = ['all', 'pending', 'duplicate', 'processed'];
  const tabLabels = ['All Transactions', 'Pending Review', 'Duplicates', 'Processed'];

  useEffect(() => {
    fetchTransactions();
  }, [page, rowsPerPage, debouncedSearch, activeTab]);

  const fetchTransactions = async (retryCount = 0) => {
    try {
      setLoading(true);
      const filter = tabFilters[activeTab];
      const response = await api.get('/admin/transactions', {
        params: {
          page: page + 1,
          limit: rowsPerPage,
          search: debouncedSearch,
          filter
        }
      });
      
      if (response.data && response.data.transactions) {
        setTransactions(response.data.transactions);
        setPagination(response.data.pagination);
        setStats(response.data.stats || {
          pending: 0,
          approved: 0,
          rejected: 0,
          duplicates: 0
        });
      } else {
        throw new Error('Invalid response format');
      }
    } catch (error) {
      console.error('Transaction fetch error:', error);
      
      // Retry once for network errors
      if (retryCount === 0 && (!error.status || error.status >= 500)) {
        console.log('Retrying transaction fetch...');
        setTimeout(() => fetchTransactions(1), 2000);
        return;
      }
      
      const errorMessage = error.message || 
                          error.response?.data?.message || 
                          'Failed to fetch transactions. Please refresh the page.';
      showNotification(errorMessage, 'error');
      
      // Set empty state on error
      setTransactions([]);
      setStats({
        pending: 0,
        approved: 0,
        rejected: 0,
        duplicates: 0
      });
    } finally {
      setLoading(false);
    }
  };

  const openTransactionDetails = async (transaction) => {
    try {
      const response = await api.get(`/admin/transactions/${transaction._id}`);
      setSelectedTransaction(transaction);
      setTransactionDetails(response.data);
      setDetailDialogOpen(true);
    } catch (error) {
      showNotification('Failed to fetch transaction details', 'error');
    }
  };

  const openProcessDialog = (transaction, action) => {
    setProcessingTransaction({ ...transaction, action });
    setProcessNotes('');
    setProcessDialogOpen(true);
  };

  const handleProcessTransaction = async () => {
    if (!processingTransaction) return;

    try {
      setProcessLoading(true);
      await api.put(`/admin/transactions/${processingTransaction._id}`, {
        status: processingTransaction.action,
        notes: processNotes
      });
      
      showNotification(
        `Transaction ${processingTransaction.action} successfully`,
        'success'
      );
      
      setProcessDialogOpen(false);
      fetchTransactions();
    } catch (error) {
      showNotification(
        error.response?.data?.message || 'Failed to process transaction',
        'error'
      );
    } finally {
      setProcessLoading(false);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    showNotification('Copied to clipboard', 'success');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'warning';
      case 'approved': return 'success';
      case 'rejected': return 'error';
      default: return 'default';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending': return <Schedule />;
      case 'approved': return <CheckCircle />;
      case 'rejected': return <Cancel />;
      default: return null;
    }
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    setPage(0);
  };

  const columns = [
    {
      field: 'user',
      label: 'User',
      render: (row) => (
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Avatar sx={{ mr: 2, bgcolor: 'primary.light' }}>
            <Person />
          </Avatar>
          <Box>
            <Typography variant="body2" fontWeight={600}>
              {row.user.fullName}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {row.user.email}
            </Typography>
          </Box>
        </Box>
      ),
    },
    {
      field: 'transactionId',
      label: 'Transaction ID',
      render: (row) => (
        <Box>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="body2" fontWeight={600} fontFamily="monospace">
              {row.transactionId}
            </Typography>
            <IconButton
              size="small"
              onClick={() => copyToClipboard(row.transactionId)}
              sx={{ ml: 1 }}
            >
              <ContentCopy fontSize="small" />
            </IconButton>
          </Box>
          {row.isDuplicate && (
            <Chip
              label={`${row.duplicateCount} submissions`}
              color="warning"
              size="small"
              sx={{ mt: 1 }}
            />
          )}
        </Box>
      ),
    },
    {
      field: 'amount',
      label: 'Amount',
      render: (row) => (
        <Typography variant="body2" fontWeight={600}>
          {row.amount.toLocaleString()} RWF
        </Typography>
      ),
    },
    {
      field: 'submittedAt',
      label: 'Submitted',
      render: (row) => (
        <Box>
          <Typography variant="body2">
            {format(new Date(row.submittedAt), 'MMM dd, yyyy')}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            {formatDistanceToNow(new Date(row.submittedAt), { addSuffix: true })}
          </Typography>
        </Box>
      ),
    },
    {
      field: 'status',
      label: 'Status',
      render: (row) => (
        <Chip
          icon={getStatusIcon(row.status)}
          label={row.status.charAt(0).toUpperCase() + row.status.slice(1)}
          color={getStatusColor(row.status)}
          variant={row.status === 'pending' ? 'filled' : 'outlined'}
        />
      ),
    },
    {
      field: 'actions',
      label: 'Actions',
      render: (row) => (
        <Box>
          <Tooltip title="View Details">
            <IconButton
              onClick={() => openTransactionDetails(row)}
              color="primary"
            >
              <Visibility />
            </IconButton>
          </Tooltip>
          {row.status === 'pending' && (
            <>
              <Tooltip title="Approve">
                <IconButton
                  onClick={() => openProcessDialog(row, 'approved')}
                  sx={{ color: 'success.main' }}
                >
                  <CheckCircle />
                </IconButton>
              </Tooltip>
              <Tooltip title="Reject">
                <IconButton
                  onClick={() => openProcessDialog(row, 'rejected')}
                  sx={{ color: 'error.main' }}
                >
                  <Cancel />
                </IconButton>
              </Tooltip>
            </>
          )}
        </Box>
      ),
    },
  ];

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Header */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" fontWeight={700} gutterBottom>
          🔍 Transaction Approvals
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Review and approve user transaction IDs for account activation
        </Typography>
      </Box>

      {/* Stats Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'warning.light', color: 'warning.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Schedule sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {stats.pending}
                  </Typography>
                  <Typography variant="body2">Pending Review</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'success.light', color: 'success.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <CheckCircle sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {stats.approved}
                  </Typography>
                  <Typography variant="body2">Approved</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'error.light', color: 'error.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Cancel sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {stats.rejected}
                  </Typography>
                  <Typography variant="body2">Rejected</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'info.light', color: 'info.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Warning sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {stats.duplicates}
                  </Typography>
                  <Typography variant="body2">Duplicates</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Main Content */}
      <Paper sx={{ borderRadius: 3, boxShadow: 3 }}>
        {/* Tabs */}
        <Tabs
          value={activeTab}
          onChange={handleTabChange}
          sx={{ borderBottom: 1, borderColor: 'divider', px: 3 }}
        >
          {tabLabels.map((label, index) => (
            <Tab
              key={index}
              label={
                <Badge
                  badgeContent={Object.values(stats)[index] || 0}
                  color="primary"
                  max={999}
                >
                  {label}
                </Badge>
              }
            />
          ))}
        </Tabs>

        {/* Search */}
        <Box sx={{ p: 3, borderBottom: '1px solid', borderColor: 'divider' }}>
          <TextField
            fullWidth
            placeholder="Search by transaction ID or user name..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search />
                </InputAdornment>
              ),
            }}
            sx={{
              '& .MuiOutlinedInput-root': {
                borderRadius: 2,
              }
            }}
          />
        </Box>

        {/* Table */}
        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress size={50} />
          </Box>
        ) : transactions.length === 0 ? (
          <Box sx={{ textAlign: 'center', py: 8 }}>
            <Receipt sx={{ fontSize: 80, color: 'text.disabled', mb: 2 }} />
            <Typography variant="h6" color="text.secondary">
              No transactions found
            </Typography>
          </Box>
        ) : (
          <>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    {columns.map((column) => (
                      <TableCell key={column.field} sx={{ fontWeight: 600 }}>
                        {column.label}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {transactions.map((row) => (
                    <TableRow
                      key={row._id}
                      sx={{
                        '&:hover': { bgcolor: 'action.hover' },
                        ...(row.isDuplicate && { bgcolor: 'warning.light', '&:hover': { bgcolor: 'warning.main' } })
                      }}
                    >
                      {columns.map((column) => (
                        <TableCell key={column.field}>
                          {column.render ? column.render(row) : row[column.field]}
                        </TableCell>
                      ))}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              component="div"
              count={pagination.total}
              page={page}
              onPageChange={(e, newPage) => setPage(newPage)}
              rowsPerPage={rowsPerPage}
              onRowsPerPageChange={(e) => setRowsPerPage(parseInt(e.target.value, 10))}
            />
          </>
        )}
      </Paper>

      {/* Transaction Details Dialog */}
      <Dialog
        open={detailDialogOpen}
        onClose={() => setDetailDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle sx={{ bgcolor: 'primary.light', color: 'white' }}>
          🔍 Transaction Details
        </DialogTitle>
        <DialogContent sx={{ p: 0 }}>
          {transactionDetails && (
            <Box sx={{ p: 3 }}>
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="h6" gutterBottom sx={{ color: 'primary.main' }}>
                        📋 Transaction Info
                      </Typography>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Transaction ID</Typography>
                        <Typography variant="body1" fontFamily="monospace" fontWeight={600}>
                          {transactionDetails.transaction.transactionId}
                        </Typography>
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Amount</Typography>
                        <Typography variant="body1" fontWeight={600}>
                          {transactionDetails.transaction.amount.toLocaleString()} RWF
                        </Typography>
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Status</Typography>
                        <Chip
                          label={transactionDetails.transaction.status}
                          color={getStatusColor(transactionDetails.transaction.status)}
                          size="small"
                        />
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Submitted</Typography>
                        <Typography variant="body1">
                          {format(new Date(transactionDetails.transaction.submittedAt), 'PPpp')}
                        </Typography>
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="h6" gutterBottom sx={{ color: 'success.main' }}>
                        👤 User Info
                      </Typography>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Full Name</Typography>
                        <Typography variant="body1" fontWeight={600}>
                          {transactionDetails.transaction.user.fullName}
                        </Typography>
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Email</Typography>
                        <Typography variant="body1">
                          {transactionDetails.transaction.user.email}
                        </Typography>
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Mobile</Typography>
                        <Typography variant="body1">
                          {transactionDetails.transaction.user.mobileNumber}
                        </Typography>
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">Account Status</Typography>
                        <Chip
                          label={transactionDetails.transaction.user.isActive ? 'Active' : 'Inactive'}
                          color={transactionDetails.transaction.user.isActive ? 'success' : 'warning'}
                          size="small"
                        />
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
              </Grid>

              {/* Duplicate Transactions */}
              {transactionDetails.duplicateTransactions.length > 0 && (
                <Box sx={{ mt: 3 }}>
                  <Alert severity="warning" sx={{ mb: 2 }}>
                    <Typography variant="h6" gutterBottom>
                      ⚠️ Duplicate Transaction Alert
                    </Typography>
                    This transaction ID has been submitted {transactionDetails.stats.duplicateCount} times by different users.
                  </Alert>
                  <Accordion>
                    <AccordionSummary expandIcon={<ExpandMore />}>
                      <Typography variant="h6">
                        🔄 Other Submissions ({transactionDetails.duplicateTransactions.length})
                      </Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                      <List>
                        {transactionDetails.duplicateTransactions.map((dup, index) => (
                          <ListItem key={index}>
                            <ListItemAvatar>
                              <Avatar>
                                <Person />
                              </Avatar>
                            </ListItemAvatar>
                            <ListItemText
                              primary={dup.user.fullName}
                              secondary={`${dup.user.email} • ${format(new Date(dup.submittedAt), 'PPp')}`}
                            />
                            <Chip
                              label={dup.status}
                              color={getStatusColor(dup.status)}
                              size="small"
                            />
                          </ListItem>
                        ))}
                      </List>
                    </AccordionDetails>
                  </Accordion>
                </Box>
              )}
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDetailDialogOpen(false)}>Close</Button>
          {selectedTransaction?.status === 'pending' && (
            <>
              <Button
                onClick={() => {
                  setDetailDialogOpen(false);
                  openProcessDialog(selectedTransaction, 'approved');
                }}
                variant="contained"
                color="success"
                startIcon={<CheckCircle />}
              >
                Approve
              </Button>
              <Button
                onClick={() => {
                  setDetailDialogOpen(false);
                  openProcessDialog(selectedTransaction, 'rejected');
                }}
                variant="contained"
                color="error"
                startIcon={<Cancel />}
              >
                Reject
              </Button>
            </>
          )}
        </DialogActions>
      </Dialog>

      {/* Process Transaction Dialog */}
      <Dialog
        open={processDialogOpen}
        onClose={() => setProcessDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle sx={{
          bgcolor: processingTransaction?.action === 'approved' ? 'success.light' : 'error.light',
          color: 'white'
        }}>
          {processingTransaction?.action === 'approved' ? '✅ Approve Transaction' : '❌ Reject Transaction'}
        </DialogTitle>
        <DialogContent sx={{ mt: 2 }}>
          <Typography variant="body1" gutterBottom>
            Are you sure you want to <strong>{processingTransaction?.action}</strong> this transaction?
          </Typography>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            Transaction ID: <code>{processingTransaction?.transactionId}</code>
          </Typography>
          <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
            User: {processingTransaction?.user?.fullName}
          </Typography>
          
          <TextField
            fullWidth
            multiline
            rows={3}
            label="Notes (Optional)"
            value={processNotes}
            onChange={(e) => setProcessNotes(e.target.value)}
            placeholder="Add any notes about this decision..."
            sx={{ mt: 2 }}
          />

          {processingTransaction?.action === 'approved' && (
            <Alert severity="info" sx={{ mt: 2 }}>
              <Typography variant="body2">
                Approving this transaction will:
                • Activate the user's account
                • Process referral bonuses (if applicable)
                • Send approval notification to user
              </Typography>
            </Alert>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setProcessDialogOpen(false)} disabled={processLoading}>
            Cancel
          </Button>
          <Button
            onClick={handleProcessTransaction}
            variant="contained"
            color={processingTransaction?.action === 'approved' ? 'success' : 'error'}
            disabled={processLoading}
            startIcon={processLoading ? <CircularProgress size={16} /> : 
              processingTransaction?.action === 'approved' ? <CheckCircle /> : <Cancel />}
          >
            {processLoading ? 'Processing...' : 
             processingTransaction?.action === 'approved' ? 'Approve' : 'Reject'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}
