import { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  IconButton,
  Chip,
  CircularProgress,
  TextField,
  InputAdornment,
  Button,
  Tooltip,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Grid,
  Card,
  CardContent,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Divider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  LinearProgress
} from '@mui/material';
import {
  TrendingUp,
  Search,
  Visibility,
  EmojiEvents,
  Person,
  MonetizationOn,
  Group,
  Star,
  AccountBalanceWallet,
  ExpandMore,
  FilterList,
  RefreshRounded,
  Timeline,
  AssessmentRounded
} from '@mui/icons-material';
import { format } from 'date-fns';
import api from '../../utils/api';
import { useNotification } from '../../context/NotificationContext';
import { useDebounce } from '../../hooks/useDebounce';

export default function Leaderboard() {
  const [referrers, setReferrers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [search, setSearch] = useState('');
  const [timeRange, setTimeRange] = useState('all');
  const [sortBy, setSortBy] = useState('totalEarnings');
  const [pagination, setPagination] = useState({ page: 0, limit: 10, total: 0 });
  const [topPerformers, setTopPerformers] = useState([]);
  const [stats, setStats] = useState({
    totalReferrers: 0,
    totalReferrals: 0,
    totalEarnings: 0,
    activeReferrers: 0
  });
  
  // Detailed view
  const [detailDialogOpen, setDetailDialogOpen] = useState(false);
  const [selectedReferrer, setSelectedReferrer] = useState(null);
  const [referrerDetails, setReferrerDetails] = useState(null);

  const { showNotification } = useNotification();
  const debouncedSearch = useDebounce(search, 500);

  const timeRanges = [
    { value: 'all', label: 'All Time' },
    { value: '7d', label: 'Last 7 Days' },
    { value: '30d', label: 'Last 30 Days' },
    { value: '90d', label: 'Last 3 Months' },
    { value: '1y', label: 'Last Year' }
  ];

  const sortOptions = [
    { value: 'totalEarnings', label: 'Total Earnings' },
    { value: 'totalReferrals', label: 'Total Referrals' },
    { value: 'activeReferrals', label: 'Active Referrals' },
    { value: 'conversionRate', label: 'Conversion Rate' }
  ];

  useEffect(() => {
    fetchLeaderboard();
  }, [page, rowsPerPage, debouncedSearch, timeRange, sortBy]);

  const fetchLeaderboard = async (retryCount = 0) => {
    try {
      setLoading(true);
      const response = await api.get('/admin/leaderboard', {
        params: {
          page: page + 1,
          limit: rowsPerPage,
          search: debouncedSearch,
          timeRange,
          sortBy
        }
      });
      
      if (response.data && response.data.referrers) {
        setReferrers(response.data.referrers);
        setPagination(response.data.pagination);
        setTopPerformers(response.data.topPerformers || []);
        setStats(response.data.stats || {
          totalReferrers: 0,
          totalReferrals: 0,
          totalEarnings: 0,
          activeReferrers: 0
        });
      } else {
        throw new Error('Invalid response format');
      }
    } catch (error) {
      console.error('Leaderboard fetch error:', error);
      
      // Retry once for network errors
      if (retryCount === 0 && (!error.status || error.status >= 500)) {
        console.log('Retrying leaderboard fetch...');
        setTimeout(() => fetchLeaderboard(1), 2000);
        return;
      }
      
      const errorMessage = error.message || 
                          error.response?.data?.message || 
                          'Failed to fetch leaderboard data. Please refresh the page.';
      showNotification(errorMessage, 'error');
      
      // Set empty state on error
      setReferrers([]);
      setTopPerformers([]);
      setStats({
        totalReferrers: 0,
        totalReferrals: 0,
        totalEarnings: 0,
        activeReferrers: 0
      });
    } finally {
      setLoading(false);
    }
  };

  const openReferrerDetails = async (referrer) => {
    try {
      const response = await api.get(`/admin/referrers/${referrer.userId}`);
      setSelectedReferrer(referrer);
      setReferrerDetails(response.data);
      setDetailDialogOpen(true);
    } catch (error) {
      showNotification('Failed to fetch referrer details', 'error');
    }
  };

  const getRankIcon = (rank) => {
    if (rank === 1) return <EmojiEvents sx={{ color: '#FFD700' }} />;
    if (rank === 2) return <EmojiEvents sx={{ color: '#C0C0C0' }} />;
    if (rank === 3) return <EmojiEvents sx={{ color: '#CD7F32' }} />;
    return <Star sx={{ color: 'primary.main' }} />;
  };

  const getRankColor = (rank) => {
    if (rank <= 3) return 'primary';
    if (rank <= 10) return 'secondary';
    return 'default';
  };

  const formatEarnings = (amount) => {
    return `RWF ${amount.toLocaleString()}`;
  };

  const getConversionRate = (activeReferrals, totalReferrals) => {
    if (!totalReferrals) return 0;
    return ((activeReferrals / totalReferrals) * 100).toFixed(1);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const columns = [
    {
      field: 'rank',
      label: 'Rank',
      render: (row, index) => (
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          {getRankIcon(page * rowsPerPage + index + 1)}
          <Typography variant="h6" fontWeight={700} sx={{ ml: 1 }}>
            #{page * rowsPerPage + index + 1}
          </Typography>
        </Box>
      ),
    },
    {
      field: 'user',
      label: 'Referrer',
      render: (row) => (
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Avatar sx={{ mr: 2, bgcolor: 'primary.light' }}>
            <Person />
          </Avatar>
          <Box>
            <Typography variant="body1" fontWeight={600}>
              {row.user.fullName}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {row.user.email}
            </Typography>
            <Typography variant="caption" display="block" color="text.secondary">
              Joined: {format(new Date(row.user.createdAt), 'MMM yyyy')}
            </Typography>
          </Box>
        </Box>
      ),
    },
    {
      field: 'referrals',
      label: 'Referrals',
      render: (row) => (
        <Box>
          <Typography variant="h6" fontWeight={700} color="primary.main">
            {row.totalReferrals}
          </Typography>
          <Typography variant="body2" color="success.main">
            {row.activeReferrals} active
          </Typography>
          <Typography variant="caption" color="text.secondary">
            {getConversionRate(row.activeReferrals, row.totalReferrals)}% conversion
          </Typography>
        </Box>
      ),
    },
    {
      field: 'earnings',
      label: 'Total Earnings',
      render: (row) => (
        <Box>
          <Typography variant="h6" fontWeight={700} color="success.main">
            {formatEarnings(row.totalEarnings)}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {formatEarnings(row.recentEarnings || 0)} recent
          </Typography>
        </Box>
      ),
    },
    {
      field: 'performance',
      label: 'Performance',
      render: (row) => (
        <Box sx={{ minWidth: 120 }}>
          <Typography variant="caption" color="text.secondary">
            Activity Score
          </Typography>
          <LinearProgress
            variant="determinate"
            value={Math.min((row.activityScore || 0), 100)}
            sx={{
              height: 8,
              borderRadius: 4,
              backgroundColor: 'grey.200',
              '& .MuiLinearProgress-bar': {
                backgroundColor: row.activityScore > 80 ? 'success.main' : 
                                row.activityScore > 60 ? 'warning.main' : 'error.main'
              }
            }}
          />
          <Typography variant="caption">
            {row.activityScore || 0}%
          </Typography>
        </Box>
      ),
    },
    {
      field: 'actions',
      label: 'Actions',
      render: (row) => (
        <Box>
          <Tooltip title="View Details">
            <IconButton
              onClick={() => openReferrerDetails(row)}
              color="primary"
            >
              <Visibility />
            </IconButton>
          </Tooltip>
        </Box>
      ),
    },
  ];

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Header */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" fontWeight={700} gutterBottom>
          🏆 Referrer Leaderboard
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Track and analyze referrer performance and earnings across the platform
        </Typography>
      </Box>

      {/* Stats Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'primary.light', color: 'primary.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Group sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {stats.totalReferrers}
                  </Typography>
                  <Typography variant="body2">Total Referrers</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'success.light', color: 'success.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <TrendingUp sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {stats.totalReferrals}
                  </Typography>
                  <Typography variant="body2">Total Referrals</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'warning.light', color: 'warning.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <MonetizationOn sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {formatEarnings(stats.totalEarnings)}
                  </Typography>
                  <Typography variant="body2">Total Earnings</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'info.light', color: 'info.contrastText' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <AssessmentRounded sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h4" fontWeight={700}>
                    {stats.activeReferrers}
                  </Typography>
                  <Typography variant="body2">Active This Month</Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Top Performers */}
      {topPerformers.length > 0 && (
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12}>
            <Paper sx={{ borderRadius: 3, p: 3 }}>
              <Typography variant="h6" fontWeight={700} gutterBottom>
                🌟 Top Performers This Month
              </Typography>
              <Grid container spacing={2}>
                {topPerformers.slice(0, 3).map((performer, index) => (
                  <Grid item xs={12} md={4} key={performer.userId}>
                    <Card 
                      sx={{ 
                        textAlign: 'center', 
                        p: 2,
                        background: index === 0 ? 'linear-gradient(135deg, #FFD700, #FFA000)' :
                                   index === 1 ? 'linear-gradient(135deg, #C0C0C0, #9E9E9E)' :
                                   'linear-gradient(135deg, #CD7F32, #8D6E63)',
                        color: 'white'
                      }}
                    >
                      <Avatar sx={{ mx: 'auto', mb: 1, width: 60, height: 60, bgcolor: 'rgba(255,255,255,0.2)' }}>
                        {getRankIcon(index + 1)}
                      </Avatar>
                      <Typography variant="h6" fontWeight={700}>
                        {performer.user.fullName}
                      </Typography>
                      <Typography variant="body2" sx={{ opacity: 0.9 }}>
                        {performer.totalReferrals} referrals
                      </Typography>
                      <Typography variant="h6" fontWeight={700}>
                        {formatEarnings(performer.totalEarnings)}
                      </Typography>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Paper>
          </Grid>
        </Grid>
      )}

      {/* Filters */}
      <Paper sx={{ borderRadius: 3, mb: 3, p: 3 }}>
        <Grid container spacing={3} alignItems="center">
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              placeholder="Search referrers..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <FormControl fullWidth>
              <InputLabel>Time Range</InputLabel>
              <Select
                value={timeRange}
                label="Time Range"
                onChange={(e) => setTimeRange(e.target.value)}
              >
                {timeRanges.map((range) => (
                  <MenuItem key={range.value} value={range.value}>
                    {range.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} md={3}>
            <FormControl fullWidth>
              <InputLabel>Sort By</InputLabel>
              <Select
                value={sortBy}
                label="Sort By"
                onChange={(e) => setSortBy(e.target.value)}
              >
                {sortOptions.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} md={2}>
            <Button
              fullWidth
              variant="outlined"
              startIcon={<RefreshRounded />}
              onClick={fetchLeaderboard}
              sx={{ height: 56 }}
            >
              Refresh
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {/* Main Table */}
      <Paper sx={{ borderRadius: 3, boxShadow: 3 }}>
        <TableContainer>
          <Table>
            <TableHead sx={{ bgcolor: 'grey.50' }}>
              <TableRow>
                {columns.map((column) => (
                  <TableCell key={column.field}>
                    <Typography variant="subtitle2" fontWeight={700}>
                      {column.label}
                    </Typography>
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {loading ? (
                <TableRow>
                  <TableCell colSpan={columns.length} align="center">
                    <CircularProgress sx={{ my: 3 }} />
                  </TableCell>
                </TableRow>
              ) : referrers.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={columns.length} align="center">
                    <Typography variant="body1" color="text.secondary" sx={{ py: 3 }}>
                      No referrers found
                    </Typography>
                  </TableCell>
                </TableRow>
              ) : (
                referrers.map((row, index) => (
                  <TableRow key={row.userId} hover>
                    {columns.map((column) => (
                      <TableCell key={column.field}>
                        {column.render(row, index)}
                      </TableCell>
                    ))}
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </TableContainer>
        
        <TablePagination
          rowsPerPageOptions={[5, 10, 25, 50]}
          component="div"
          count={pagination.total}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>

      {/* Referrer Details Dialog */}
      <Dialog
        open={detailDialogOpen}
        onClose={() => setDetailDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Typography variant="h6" fontWeight={700}>
            Referrer Performance Details
          </Typography>
        </DialogTitle>
        <DialogContent>
          {referrerDetails && (
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Card sx={{ p: 2 }}>
                  <Typography variant="h6" gutterBottom>Basic Information</Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                    <Avatar sx={{ mr: 2, bgcolor: 'primary.light' }}>
                      <Person />
                    </Avatar>
                    <Box>
                      <Typography variant="body1" fontWeight={600}>
                        {selectedReferrer?.user.fullName}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        {selectedReferrer?.user.email}
                      </Typography>
                    </Box>
                  </Box>
                  <Divider sx={{ my: 2 }} />
                  <Typography variant="body2">
                    <strong>Account Status:</strong> {selectedReferrer?.user.isActive ? 'Active' : 'Inactive'}
                  </Typography>
                  <Typography variant="body2">
                    <strong>Joined:</strong> {format(new Date(selectedReferrer?.user.createdAt), 'MMM dd, yyyy')}
                  </Typography>
                </Card>
              </Grid>
              <Grid item xs={12} md={6}>
                <Card sx={{ p: 2 }}>
                  <Typography variant="h6" gutterBottom>Performance Metrics</Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>Total Referrals:</strong> {selectedReferrer?.totalReferrals}
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>Active Referrals:</strong> {selectedReferrer?.activeReferrals}
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>Total Earnings:</strong> {formatEarnings(selectedReferrer?.totalEarnings)}
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>Conversion Rate:</strong> {getConversionRate(selectedReferrer?.activeReferrals, selectedReferrer?.totalReferrals)}%
                  </Typography>
                </Card>
              </Grid>
              {referrerDetails.recentReferrals && (
                <Grid item xs={12}>
                  <Accordion>
                    <AccordionSummary expandIcon={<ExpandMore />}>
                      <Typography variant="h6">Recent Referrals</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                      <List>
                        {referrerDetails.recentReferrals.map((referral) => (
                          <ListItem key={referral._id}>
                            <ListItemAvatar>
                              <Avatar>
                                <Person />
                              </Avatar>
                            </ListItemAvatar>
                            <ListItemText
                              primary={referral.fullName}
                              secondary={`Joined: ${format(new Date(referral.createdAt), 'MMM dd, yyyy')} • Status: ${referral.isActive ? 'Active' : 'Inactive'}`}
                            />
                          </ListItem>
                        ))}
                      </List>
                    </AccordionDetails>
                  </Accordion>
                </Grid>
              )}
            </Grid>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDetailDialogOpen(false)}>Close</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}
